 evento NO será contemplado  ','Agustin Gutierrez','Jocelyn Vazquez',null,null,null,'18.75',null,NULL,6,0,119,null,0,2,108,'PruebasD2, ciclo4',1,NULL,NULL,NULL,NULL,0,NULL,0,'');
